import { motion, useScroll, useTransform } from 'framer-motion';
import { ArrowRight, Sparkles, Play } from 'lucide-react';
import { Link } from 'react-router-dom';
import { useState, useRef } from 'react';
import VideoPlayerModal from './VideoPlayerModal';
import ToaiHeroImg from '../assets/images/Toai_hero.png';
import FadeInSection from './animations/FadeInSection';
import HomeHeroVisual from './HomeHeroVisual';

const HeroSection = () => {
    const [isVideoOpen, setIsVideoOpen] = useState(false);
    const containerRef = useRef(null);
    const { scrollYProgress } = useScroll({
        target: containerRef,
        offset: ["start start", "end start"]
    });

    const yBackground = useTransform(scrollYProgress, [0, 1], ["0%", "50%"]);
    const opacityBackground = useTransform(scrollYProgress, [0, 0.5], [1, 0]);

    return (
        <>
            <section ref={containerRef} className="relative pt-20 sm:pt-24 md:pt-28 pb-4 sm:pb-6 md:pb-8 overflow-hidden bg-gradient-to-br from-indigo-50 via-white to-rose-50 border-b border-indigo-50">
                {/* Background Blobs - Parallax Effect */}
                <motion.div
                    style={{ y: yBackground, opacity: opacityBackground }}
                    className="absolute inset-0 w-full h-full overflow-hidden pointer-events-none"
                >
                    <motion.div
                        animate={{
                            scale: [1, 1.2, 1],
                            rotate: [0, 10, 0],
                        }}
                        transition={{
                            duration: 20,
                            repeat: Infinity,
                            repeatType: "reverse"
                        }}
                        className="absolute top-0 right-0 -translate-y-1/4 translate-x-1/4 w-[400px] sm:w-[600px] lg:w-[800px] h-[400px] sm:h-[600px] lg:h-[800px] bg-indigo-200/30 rounded-full blur-3xl -z-10"
                    />
                    <motion.div
                        animate={{
                            scale: [1, 1.1, 1],
                            rotate: [0, -10, 0],
                        }}
                        transition={{
                            duration: 15,
                            repeat: Infinity,
                            repeatType: "reverse",
                            delay: 2
                        }}
                        className="absolute bottom-0 left-0 translate-y-1/4 -translate-x-1/4 w-[300px] sm:w-[450px] lg:w-[600px] h-[300px] sm:h-[450px] lg:h-[600px] bg-rose-100/40 rounded-full blur-3xl -z-10"
                    />
                </motion.div>

                <div className="container mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
                    <div className="flex flex-col lg:flex-row items-center gap-6 sm:gap-8 lg:gap-12">
                        <div className="lg:w-1/2 space-y-4 sm:space-y-6 text-center lg:text-left">
                            <FadeInSection delay={0.1}>
                                <div className="inline-flex items-center space-x-2 bg-white/80 backdrop-blur-sm px-3 sm:px-4 py-1.5 sm:py-2 rounded-full text-indigo-700 font-semibold text-xs sm:text-sm border border-indigo-100 shadow-sm">
                                    <Sparkles className="w-3 h-3 sm:w-4 sm:h-4" />
                                    <span>Enterprise AI Foundation Layer</span>
                                </div>
                            </FadeInSection>

                            <FadeInSection delay={0.2}>
                                <h1 className="text-5xl sm:text-6xl md:text-7xl lg:text-7xl xl:text-8xl font-black text-slate-900 leading-[0.85] tracking-tighter mb-8 perspective-1000">
                                    The <br />
                                    <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 via-indigo-700 to-indigo-900 drop-shadow-sm italic">Enterprise AI</span> <br />
                                    Kernel.
                                </h1>
                            </FadeInSection>

                            <FadeInSection delay={0.3}>
                                <p className="text-xl sm:text-2xl text-slate-500 leading-relaxed max-w-2xl mx-auto lg:mx-0 font-medium">
                                    Connect. Normalize. Automate. <br />
                                    The foundational intelligence layer that unifies your entire organization's data ecosystem.
                                </p>
                            </FadeInSection>

                            <FadeInSection delay={0.4}>
                                <div className="flex flex-col sm:flex-row justify-center lg:justify-start gap-3 sm:gap-4 pt-2">
                                    <motion.button
                                        whileHover={{ scale: 1.05 }}
                                        whileTap={{ scale: 0.95 }}
                                        onClick={() => setIsVideoOpen(true)}
                                        className="w-full sm:w-auto bg-white text-indigo-600 px-5 sm:px-6 py-2.5 sm:py-3.5 rounded-xl text-base font-bold shadow-xl shadow-indigo-100 hover:shadow-indigo-200 hover:bg-indigo-50 transition-all border border-indigo-100/50 flex items-center justify-center space-x-2"
                                    >
                                        <Play className="w-4 h-4 sm:w-5 sm:h-5 fill-indigo-600" />
                                        <span>Watch Demo</span>
                                    </motion.button>
                                    <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }} className="w-full sm:w-auto">
                                        <Link
                                            to="/contact"
                                            className="w-full sm:w-auto bg-white border-2 border-slate-200 text-slate-700 px-5 sm:px-6 py-2.5 sm:py-3.5 rounded-xl text-base font-bold hover:border-indigo-600 hover:text-indigo-600 transition-colors hover:bg-indigo-50 flex items-center justify-center space-x-2"
                                        >
                                            <span>Get Started</span>
                                            <ArrowRight className="w-4 h-4 sm:w-5 sm:h-5" />
                                        </Link>
                                    </motion.div>
                                </div>
                            </FadeInSection>
                        </div>

                        <div className="lg:w-1/2 relative w-full">
                            <FadeInSection delay={0.4} scale>
                                <HomeHeroVisual />
                            </FadeInSection>
                        </div>
                    </div>
                </div>
            </section>

            {/* Video Player Modal */}
            <VideoPlayerModal
                isOpen={isVideoOpen}
                onClose={() => setIsVideoOpen(false)}
                videoSrc="/src/assets/videos/demo_video.mp4"
            />
        </>
    );
};

export default HeroSection;

import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    Cpu,
    Database,
    ShieldCheck,
    Network,
    Zap,
    Brain,
    Activity,
    ScanLine,
    Terminal
} from 'lucide-react';

const HomeHeroVisual = () => {
    const [isHovered, setIsHovered] = useState(false);

    return (
        <div
            className="relative w-full aspect-square lg:aspect-video flex items-center justify-center cursor-none select-none"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
        >
            {/* 3D Floor Perspective */}
            <div className="absolute inset-x-0 bottom-0 h-1/2 [perspective:1000px] pointer-events-none overflow-hidden h-[300px]">
                <motion.div
                    initial={{ rotateX: 60, y: 100, opacity: 0 }}
                    animate={{ rotateX: 60, y: 0, opacity: 0.1 }}
                    transition={{ duration: 1.5 }}
                    className="absolute inset-x-[-100%] top-0 h-[200%] border-t border-indigo-500/40"
                    style={{
                        backgroundImage: `linear-gradient(rgba(99,102,241,0.2) 1.5px, transparent 1.5px), linear-gradient(90deg, rgba(99,102,241,0.2) 1.5px, transparent 1.5px)`,
                        backgroundSize: '60px 60px',
                    }}
                />
                <div className="absolute inset-0 bg-gradient-to-t from-white via-white/80 to-transparent" />
            </div>

            {/* Background Ambient Orbs */}
            <div className="absolute inset-0 pointer-events-none">
                <motion.div
                    animate={{
                        scale: [1, 1.2, 1],
                        opacity: [0.1, 0.2, 0.1],
                        x: [0, 40, 0]
                    }}
                    transition={{ duration: 10, repeat: Infinity }}
                    className="absolute top-1/4 right-1/4 w-96 h-96 bg-indigo-500/20 rounded-full blur-[120px]"
                />
                <motion.div
                    animate={{
                        scale: [1, 1.1, 1],
                        opacity: [0.05, 0.1, 0.05],
                        x: [0, -30, 0]
                    }}
                    transition={{ duration: 12, repeat: Infinity, delay: 1 }}
                    className="absolute bottom-1/4 left-1/4 w-80 h-80 bg-rose-500/15 rounded-full blur-[100px]"
                />
            </div>

            {/* Central OS Core */}
            <div className="relative z-10 w-64 h-64 sm:w-80 sm:h-80 flex items-center justify-center">

                {/* Rotating Tech Layers */}
                {[...Array(3)].map((_, i) => (
                    <motion.div
                        key={i}
                        animate={{
                            rotate: i % 2 === 0 ? 360 : -360,
                            scale: isHovered ? (1 + (i + 1) * 0.05) : 1
                        }}
                        transition={{
                            rotate: { duration: 20 + i * 10, repeat: Infinity, ease: 'linear' },
                            scale: { type: 'spring', stiffness: 100 }
                        }}
                        className={`absolute rounded-full border border-indigo-500/${10 + i * 10} flex items-center justify-center`}
                        style={{
                            width: `${100 - i * 15}%`,
                            height: `${100 - i * 15}%`,
                            borderStyle: i === 1 ? 'dashed' : 'solid'
                        }}
                    >
                        {/* Interactive Nodes on Rings */}
                        {isHovered && i === 0 && (
                            <motion.div
                                animate={{ opacity: [0, 1, 0] }}
                                transition={{ duration: 2, repeat: Infinity }}
                                className="absolute top-0 w-2 h-2 bg-indigo-500 rounded-full shadow-[0_0_10px_#4f46e5]"
                            />
                        )}
                    </motion.div>
                ))}

                {/* The Core Hub */}
                <motion.div
                    whileHover={{ scale: 1.05 }}
                    className="relative w-32 h-32 sm:w-40 sm:h-40 bg-white rounded-[2.5rem] shadow-2xl border border-indigo-100 flex items-center justify-center overflow-hidden z-20"
                >
                    <div className="absolute inset-0 bg-gradient-to-br from-indigo-50/50 via-white to-rose-50/30" />

                    {/* Inner Logic Core */}
                    <div className="relative z-10 w-20 h-20 sm:w-24 sm:h-24 bg-slate-900 rounded-[2rem] flex flex-col items-center justify-center shadow-inner overflow-hidden group">
                        <motion.div
                            animate={{ opacity: [0.3, 0.6, 0.3] }}
                            transition={{ duration: 4, repeat: Infinity }}
                            className="absolute inset-0 bg-indigo-600/20 blur-xl"
                        />

                        <Brain className="w-8 h-8 sm:w-10 sm:h-10 text-white mb-2 relative z-10" />
                        <div className="text-[8px] font-black text-indigo-400 tracking-[0.3em] uppercase relative z-10">Kern_Active</div>

                        {/* Quantum Scanning Line */}
                        <motion.div
                            animate={{ y: [-50, 50] }}
                            transition={{ duration: 3, repeat: Infinity, ease: 'linear' }}
                            className="absolute inset-x-0 h-px bg-indigo-400/30 blur-[1px]"
                        />
                    </div>
                </motion.div>

                {/* Data Stream Particles */}
                <AnimatePresence>
                    {isHovered && [...Array(8)].map((_, i) => {
                        const angle = (i * 45) * (Math.PI / 180);
                        return (
                            <motion.div
                                key={i}
                                initial={{ opacity: 0, scale: 0, x: Math.cos(angle) * 150, y: Math.sin(angle) * 150 }}
                                animate={{ opacity: 1, scale: 1, x: 0, y: 0 }}
                                exit={{ opacity: 0, scale: 0 }}
                                transition={{
                                    duration: 1.5,
                                    repeat: Infinity,
                                    delay: i * 0.2,
                                    ease: "circOut"
                                }}
                                className="absolute w-1 h-1 bg-indigo-400 rounded-full shadow-[0_0_8px_#818cf8]"
                            />
                        );
                    })}
                </AnimatePresence>
            </div>

            {/* Tech HUD Floating Overlays */}
            <motion.div
                animate={{ y: [0, -10, 0] }}
                transition={{ duration: 5, repeat: Infinity }}
                className="absolute left-10 lg:left-0 top-1/4 p-4 bg-white/80 backdrop-blur-md rounded-2xl border border-indigo-100 shadow-xl hidden md:block"
            >
                <div className="flex items-center gap-3 mb-2">
                    <Activity className="w-4 h-4 text-emerald-500" />
                    <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Neural_Sync</span>
                </div>
                <div className="h-1.5 w-24 bg-slate-100 rounded-full overflow-hidden">
                    <motion.div
                        animate={{ x: ['-100%', '100%'] }}
                        transition={{ duration: 2, repeat: Infinity }}
                        className="h-full w-1/2 bg-emerald-500/50"
                    />
                </div>
            </motion.div>

            <motion.div
                animate={{ y: [0, 10, 0] }}
                transition={{ duration: 4, repeat: Infinity }}
                className="absolute right-10 lg:right-0 bottom-1/4 p-4 bg-slate-900 rounded-2xl border border-white/10 shadow-2xl hidden md:block"
            >
                <div className="flex items-center gap-2 mb-2 text-indigo-400">
                    <Terminal className="w-3 h-3" />
                    <span className="text-[8px] font-mono uppercase tracking-widest">System_Access</span>
                </div>
                <div className="space-y-1">
                    <div className="w-16 h-1 bg-white/10 rounded-full" />
                    <div className="w-12 h-1 bg-white/10 rounded-full" />
                    <div className="w-20 h-1 bg-indigo-500/40 rounded-full" />
                </div>
            </motion.div>

            {/* Mouse Pointer Accent */}
            <motion.div
                animate={{
                    scale: isHovered ? 1.5 : 1,
                    opacity: isHovered ? 0.8 : 0.3
                }}
                className="absolute pointer-events-none w-12 h-12 border border-indigo-500/40 rounded-full"
            />
        </div>
    );
};

export default HomeHeroVisual;

